class MessageGeneratorTestCase(TestCase):
    def test_humanize_key(self):
        self.assertEquals(MessageGenerator.humanize_key("start_time"), "Start Time")
        self.assertEquals(MessageGenerator.humanize_key("start_time_again"), "Start Time Again")
        self.assertEquals(MessageGenerator.humanize_key("race"), "Race")

        # Test special case overrides
        self.assertEquals(MessageGenerator.humanize_key("url"), "Registration Link")

class NYRRMessageGeneratorTestCase(TestCase):
    def test_empty_records(self):
        result = MessageGenerator.generate_nyrr_message([], {})
        self.assertFalse(result)

    def test_single_new_record_with_url(self):
        race = ScrapedRace.objects.create(
            title="Test Race",
            status="Closed",
            start_date="January 1, 2022",
            start_time="9:00AM",
            location="Central Park",
            url="https://meltdownlabs.com",
        )

        message = MessageGenerator.generate_nyrr_message([race], {})
        self.assertIn("New Race Added", message)
        self.assertNotIn("Registration link not yet available", message)
        self.assertNotIn("Updated Race", message)
        self.assertIn(race.title, message)
        self.assertIn(race.status, message)
        self.assertIn(race.start_date, message)
        self.assertIn(race.start_time, message)
        self.assertIn(race.location, message)
        self.assertIn(race.url, message)

    def test_single_new_record_without_url(self):
        race = ScrapedRace.objects.create(
            title="Test Race",
            status="Closed",
            start_date="January 1, 2022",
            start_time="9:00AM",
            location="Central Park",
        )

        message = MessageGenerator.generate_nyrr_message([race], {})
        self.assertIn("New Race Added", message)
        self.assertIn("Registration link not yet available", message)
        self.assertNotIn("Updated Race", message)
        self.assertIn(race.title, message)
        self.assertIn(race.status, message)
        self.assertIn(race.start_date, message)
        self.assertIn(race.start_time, message)
        self.assertIn(race.location, message)

    def test_single_update_removed(self):
        updates = {
            "Pride Run 4M 2022": {
                "location": ("Prospect Park", "")
            }
        }

        message = MessageGenerator.generate_nyrr_message([], updates)

        self.assertIn("Location was removed. Was previously Prospect Park", message)
        self.assertNotIn("was added", message)
        self.assertNotIn("changed from", message)

    def test_single_update_info_added(self):
        updates = {
            "Pride Run 4M 2022": {
                "location": ("", "Prospect Park")
            }
        }

        message = MessageGenerator.generate_nyrr_message([], updates)

        self.assertNotIn("was removed", message)
        self.assertNotIn("changed from", message)
        self.assertIn("Location was added: Prospect Park", message)

    def test_single_update_info_changed(self):
        updates = {
            "Pride Run 4M 2022": {
                "location": ("Central Park", "Prospect Park")
            }
        }

        message = MessageGenerator.generate_nyrr_message([], updates)

        self.assertNotIn("was removed", message)
        self.assertIn("Location changed from ~~Central Park~~ to Prospect Park", message)
        self.assertNotIn("was added", message)

