class VolunteerOpportunityWebhookTestCase(TestCase):
    def test_webhook_happy(self):
        client = APIClient()
        Parsed = [
            {
                "date": "2019-01-01",
                "time": "10:00",
                "location": "Central Park",
                "title": "Bag Check",
                "event": "Achilles 4M",
                "desc": "Lorem ipsum",
                "tags":["thing", "thing2"],
                "html": "<p>HTML</p>"
            },
            {
                "date": "2019-01-02",
                "time": "11:00",
                "location": "Prospect Park",
                "title": "Finish Line",
                "event": "Some Other Race 10M",
                "desc": "Battle hill makes me sad",
                "tags":["thing", "thing2"],
                "html": "<p>HTML</p>"
            }
        ]

        request_body = {
            "eligibles": [
                { "Parsed": p } for p in Parsed
            ],
        }

        with patch("requests.post") as requests_post:
            response = client.post(reverse('volunteer-opportunities-webhook-list'), request_body, format='json')
            requests_post.assert_called_once()

        self.assertEqual(response.status_code, HTTPStatus.NO_CONTENT)

        for p in Parsed:
            opp = VolunteerOpportunity.objects.get(start_date=p["date"])
            self.assertEqual(opp.start_date, p["date"])
            self.assertEqual(opp.start_time, p["time"])
            self.assertEqual(opp.location, p["location"])
            self.assertEqual(opp.title, p["title"])
            self.assertEqual(opp.description, p["desc"])
            self.assertEqual(opp.event, p["event"])

        response = client.get(reverse('volunteer-opportunities-list'))
        self.assertEqual(response.status_code, HTTPStatus.OK)
        body = response.json()
        self.assertEqual(len(body), 2)

class PruneVolunteerOpportunitiesTestCase(TestCase):
    def test_prune(self):
        from datetime import timedelta
        from django.utils import timezone
        opp = VolunteerOpportunity.objects.create()
        opp.created = timezone.now() - timedelta(hours=25)
        opp.save()

        newer_opp = VolunteerOpportunity.objects.create()
        newer_opp.created = timezone.now() - timedelta(hours=23)
        newer_opp.save()

        call_command('prune_volunteer_opportunities')

        with self.assertRaises(VolunteerOpportunity.DoesNotExist):
            opp.refresh_from_db()

        newer_opp.refresh_from_db()